INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '001', 'Gérant de station-service', 'Responsable de la gestion globale de la station, y compris la gestion du personnel, des stocks, des opérations quotidiennes et de la rentabilité');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '002', 'Employé de caisse', 'En charge de la caisse, des transactions financières avec les clients, de l''encaissement des paiements pour le carburant, les produits en vente et autres services.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '003', 'Pompiste', 'Chargé de faire le plein de carburant pour les clients, de vérifier les niveaux d''huile et d''eau, de nettoyer les pare-brises et de fournir un service client amical.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '004', 'Responsable des lubrifiants', ' Conseille les clients sur les produits lubrifiants et d''entretien, effectue les vidanges d''huile et autres services connexes.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '005', 'Agent de maintenance', 'S''occupe de l''entretien général de la station, des pompes à carburant, des équipements et de la maintenance de base des installations.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '006', 'Responsable des stocks', 'Gère les niveaux de stock de carburant, de lubrifiants, de produits en vente et d''autres articles connexes, en passant des commandes auprès des fournisseurs lorsque nécessaire.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '007', 'Agent marketing', 'Met en place des promotions, des offres spéciales et des campagnes publicitaires pour attirer les clients à la station.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '008', 'Responsable des ressources humaines', 'S''occupe du recrutement, de la formation, de la gestion des horaires et des questions liées au personnel.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '009', 'Agent de service clientèle', 'Répond aux questions des clients, résout les problèmes, fournit des informations sur les produits et les services offerts à la station.');
INSERT INTO position (id, code, designation, description) VALUES (nextval('position_id_seq'), '010', 'Responsable environnemental', 'Veille au respect des réglementations environnementales, notamment en ce qui concerne la manipulation et le stockage sécuritaire des carburants et autres produits potentiellement dangereux.Ces rôles peuvent varier en fonction de la taille et du type de la station-service, ainsi que des services supplémentaires qu''elle peut offrir, tels que les stations avec des boutiques de convenience, des ateliers de réparation automobile, etc.');
